import { useState } from "react";

const SimpleInput = (props) => {
  // Field value
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  // Field Touch
  const [nameIsTouched, setNameIsTouched] = useState(false);
  const [emailIsTouched, setEmailIsTouched] = useState(false);

  // Field value Validation
  const nameIsValid = input.trim() !== "";
  const emailIsValid =
    input.trim() !== "" ||
    input.trim().includes("@") ||
    input.trim().includes(".");

  // Field Validation on blur
  const nameInputIsValid = !nameIsValid && nameIsTouched;
  const emailInputIsValid = !emailIsValid && nameIsTouched;

  // Form Validation
  let formIsValid = false;

  if (nameInputIsValid && emailIsValid) {
    formIsValid = true;
  }

  //On Field Change Update
  const NameInputCHangeHandler = (event) => {
    setName(event.target.value);
  };

  //On Field blur Update
  const nameInputBlurHandler = () => {
    setNameIsTouched(true);
  };

  //On Form Submit
  const formSubmitHandler = (event) => {
    event.preventDefault();
    setNameIsTouched(true);
    if (!nameIsValid) return;
    console.log(input);
    setName("");
    setNameIsTouched(false);
  };

  const nameInputClasses = nameInputIsValid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          onInput={NameInputCHangeHandler}
          onBlur={nameInputBlurHandler}
          id="name"
        />
      </div>
      {!nameIsValid && nameIsTouched && <p>Please Enter Valid Input</p>}

      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          onInput={NameInputCHangeHandler}
          onBlur={nameInputBlurHandler}
          id="name"
        />
      </div>
      {!nameIsValid && nameIsTouched && <p>Please Enter Valid Input</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
