import useValidator from "../Hooks/use-validator";

const BasicForm = (props) => {
  const {
    value: lname,
    inputIsValid: isLNameValid,
    hasError: lnameHasError,
    valueChangeHandler: lnameValueChange,
    inputBlurHandler: lnameBlurHandler,
    reset: lnameReset,
  } = useValidator((value) => value.trim() !== "");

  const {
    value: fname,
    inputIsValid: isFnameValid,
    hasError: fnameHasError,
    valueChangeHandler: fnameValueChange,
    inputBlurHandler: fnameBlurHandler,
    reset: fnameReset,
  } = useValidator((value) => value.trim() !== "");

  const {
    value: email,
    inputIsValid: isEmailValid,
    hasError: emailHasError,
    valueChangeHandler: emailValueChange,
    inputBlurHandler: emailBlurHandler,
    reset: emailReset,
  } = useValidator(
    (value) =>
      value.trim() !== "" &&
      value.trim().includes("@") &&
      value.trim().includes(".")
  );

  const formIsValid = isLNameValid && isFnameValid && isEmailValid;

  const formSubmitHandler = (event) => {
    event.preventDefault();
    fnameBlurHandler();
    lnameBlurHandler();
    emailBlurHandler();
    if (!formIsValid) return;
    console.log("Submitted");
    lnameReset();
    fnameReset();
    emailReset();
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div
          className={!fnameHasError ? "form-control" : "form-control invalid"}
        >
          <label htmlFor="name">First Name</label>
          <input
            value={fname}
            onChange={fnameValueChange}
            onBlur={fnameBlurHandler}
            type="text"
            id="name"
          />
        </div>
        <div
          className={!lnameHasError ? "form-control" : "form-control invalid"}
        >
          <label htmlFor="name">Last Name</label>
          <input
            value={lname}
            onChange={lnameValueChange}
            onBlur={lnameBlurHandler}
            type="text"
            id="name"
          />
        </div>
      </div>
      <div className={!emailHasError ? "form-control" : "form-control invalid"}>
        <label htmlFor="name">E-Mail Address</label>
        <input
          value={email}
          onChange={emailValueChange}
          onBlur={emailBlurHandler}
          type="text"
          id="name"
        />
      </div>
      <div className="form-actions">
        <button disabled="disabled">Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
