import useValidator from "../Hooks/use-validator";

const BasicForm = (props) => {
  const {
    value: lname,
    inputIsValid: lisNameValid,
    hasError: lnameHasError,
    valueChangeHandler: lnameValueChange,
    inputBlurHandler: lnameBlurHandler,
    reset: lnameReset,
  } = useValidator((value) => value.trim() !== "");

  const {
    value: fname,
    inputIsValid: isFnameValid,
    hasError: fnameHasError,
    valueChangeHandler: fnameValueChange,
    inputBlurHandler: fnameBlurHandler,
    reset: fnameReset,
  } = useValidator((value) => value.trim() !== "");

  const {
    value: name,
    inputIsValid: isNameValid,
    hasError: nameHasError,
    valueChangeHandler: nameValueChange,
    inputBlurHandler: nameBlurHandler,
    reset: nameReset,
  } = useValidator((value) => value.trim() !== "");

  const formSubmitHandler = (event) => {
    event.preventDefault();
    fnameBlurHandler();
    lnameBlurHandler();
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div
          className={!fnameHasError ? "form-control" : "form-control invalid"}
        >
          <label htmlFor="name">First Name</label>
          <input
            value={fname}
            onChange={fnameValueChange}
            onBlur={fnameBlurHandler}
            type="text"
            id="name"
          />
        </div>
        <div
          className={!lnameHasError ? "form-control" : "form-control invalid"}
        >
          <label htmlFor="name">Last Name</label>
          <input
            value={lname}
            onChange={lnameValueChange}
            onBlur={lnameBlurHandler}
            type="text"
            id="name"
          />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
